function init(){new ymaps.Map("map",{center:[45.043746,38.943404],zoom:18}).controls.add("zoomControl",{left:15,top:15})}document.querySelector(".contacts-button").onclick=function(e){e.preventDefault();let o=document.querySelector(".feedback");o.classList.contains("feedback-form-show")?o.classList.remove("feedback-form-show"):o.classList.add("feedback-form-show")},document.querySelector(".close-feedback").onclick=function(e){e.preventDefault(),document.querySelector(".feedback").classList.remove("feedback-form-show")},ymaps.ready(init);